import { useEffect, useState } from "react";
import { Formik, Field, Form } from "formik";
import axios from "axios";

const RegestrationPage = () => {
  const [errMessage, setErrMessage] = useState(``);
  const [value, setValue] = useState(null);
  const [items, setItems] = useState();

  // useEffect(() => {
  //   fetch("/api/db/user")
  //     .then((res) => res.json())
  //     .then((data) => setItems(data.items));
  // }, []);

  // useEffect(() => {
  //   axios.post("/api/db/user").then((res) => res.send(value));
  // }, [value]);

  const sendValue = async () => {
    await axios.post("/api/db/user", "hi from front");
    // await url.json(value);
  };
  // console.log(items);
  useEffect(() => {
    const checkPwd = () => {
      if (value && value.password !== value.repeatPassword) {
        console.log("Error pwd");
        setErrMessage(`Passwords do not match`);
        return;
      }
      setErrMessage(``);
    };
    checkPwd();
    sendValue();
  }, [value]);

  return (
    <>
      <Formik
        initialValues={{
          name: "",
          email: "",
          password: "",
          repeatPassword: "",
        }}
        onSubmit={(values) => {
          alert(JSON.stringify(values, null, 2));
          console.log("1", values);
          setValue(values);
        }}
      >
        <Form>
          <Field name="name" type="text" placeholder="Name" />
          <Field name="email" type="email" placeholder="Email" />
          <Field name="password" type="password" placeholder="Password" />
          <Field
            name="repeatPassword"
            type="password"
            placeholder="Repeat password"
          />
          {errMessage}
          <button type="submit">Submit</button>
        </Form>
      </Formik>
    </>
  );
};

export default RegestrationPage;
