import React from "react";
import { Formik, Field, Form } from "formik";
import { useState, useEffect } from "react";

import getCurrencies from "../../../api/currencies";

const EurForm = () => {
  const [value, setValue] = useState();
  const [eur, setEur] = useState();
  const [uah, setUah] = useState();
  const [currency, setCurrency] = useState();
  const options = [
    {
      label: "to uah",
      value: "uah",
    },
    {
      label: "to usd",
      value: "usd",
    },
  ];
  useEffect(() => {
    async function currencyData() {
      const currencies = await getCurrencies();
      setEur(currencies[1].buy);
    }
    currencyData();
  }, []);

  useEffect(() => {
    if (value !== undefined) {
      const b = eur * value.value;
      setUah(b);
    }
  });

  return (
    <>
      <div>
        <Formik
          initialValues={{ value: "" }}
          onSubmit={async (values) => {
            await new Promise((resolve) => setTimeout(resolve, 100));
            setValue(values);
          }}
        >
          <Form className="forms">
            <Field name="value" type="number" placeholder="EUR" />

            {/* {options.map((option, key) => (
              <label key={key}>
                <Field name="currency" type="radio" value={option.value} />
                {options.label}
              </label>
            ))} */}
            <select
              id="currency"
              value={currency}
              onChange={(e) => setFruit(e.target.value)}
            >
              <option value="uah">uah</option>
              <option value="usd">usd</option>
            </select>
            <h1>Selected Fruit: {currency}</h1>

            <button type="submit">Конвертація</button>
          </Form>
        </Formik>
      </div>
      <div>{uah}</div>
    </>
  );
};

export default EurForm;
