import React from "react";
import { Formik, Field, Form } from "formik";
import { useState, useEffect } from "react";

import getCurrencies from "../../../api/currencies";

const EurForm = () => {
  const [value, setValue] = useState();
  const [eur, setEur] = useState();
  const [uah, setUah] = useState();
  const [optionValue, setOptionValue] = useState();
  useEffect(() => {
    async function currencyData() {
      const currencies = await getCurrencies();
      setEur(currencies[1].buy);
    }
    currencyData();
  }, []);

  useEffect(() => {
    if (value !== undefined) {
      const b = eur * value.value;
      setUah(b);
    }
  });

  return (
    <>
      <div>
        <Formik
          initialValues={{ value: "" }}
          onSubmit={async (values) => {
            await new Promise((resolve) => setTimeout(resolve, 100));
            setValue(values);
          }}
        >
          <Form className="forms">
            <Field name="value" type="number" placeholder="EUR" />
            <select>
              <option value={setOptionValue}>to Uah</option>
              <option value={setOptionValue}>to Usd</option>
            </select>
            <button type="submit">Конвертація</button>
          </Form>
        </Formik>
      </div>
      <div>{uah}</div>
    </>
  );
};

export default EurForm;
